#!/usr/bin/python

#PARNAME=shiftN0E1W2S3 #LOC=1,1
#PARNAME=rowlen #LOC=2,1
#PARNAME=collen #LOC=3,1

def connectionFunc( srclocs, dstlocs, shift, rowlen, collen ):

	import math

	i = 0
	out = []
	for srcloc in srclocs:
		col=i%int(rowlen)
		row=math.floor(i/rowlen)
		if shift==0:
			row=row+1
		if shift==1:
			col=col+1
		if shift==2:
			col=col-1
		if shift==3:
			row=row-1
		if col>=0 and col<rowlen and row>=0 and row<collen:
			ind = row*rowlen+col
			conn = (i,ind,0,0)
			out.append(conn)
		i = i + 1
	return out

